          {/* RSVP Section with QR Code */}
          <div>
            <div className="font-semibold mb-1">RSVP</div>
            <div className="font-medium">{form.rsvp1}</div>
            <div className="font-medium">{form.rsvp2}</div>

            {/* QR Code Section */}
            <div className="mt-2">
              <div className="w-12 h-12 border-2 border-gray-300 flex items-center justify-center mb-1 overflow-hidden">
